class Solution {
    private val cache: MutableMap<Pair<Int, Int>, Boolean> = mutableMapOf()

    fun longestPalindrome(s: String): String {
        var result = ""
        for (i in s.indices) {
            for (j in (i + 1 .. s.length)) {
                if (result.length < j - i && isPalindrome(s, i, j)) result = s.substring(i, j)
            }
        }

        return result
    }

    private fun isPalindrome(s: String, start: Int, end: Int): Boolean {
        if (cache[start to end] != null) return cache[start to end]!!
        else {
            if (start == end || start + 1 == end) {
                cache[start to end] = true
                return true
            }
            else if (s[start] == s[end - 1]) {
                cache[start to end] = isPalindrome(s, start + 1, end - 1)
                return cache[start to end]!!
            }
            else {
                cache[start to end] = false
                return false
            }
        }
    }
}